import React from 'react';
import { FaTimes, FaStar, FaShoppingCart, FaHeart } from 'react-icons/fa';
import { useCart } from '../context/CartContext';
import { useWishlist } from '../context/WishlistContext';

const QuickViewModal = ({ product, onClose }) => {
  const { addToCart } = useCart();
  const { toggleWishlist, isInWishlist } = useWishlist();

  const handleAddToCart = () => {
    addToCart(product);
    onClose();
  };

  const handleToggleWishlist = () => {
    toggleWishlist(product.id);
  };

  if (!product) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex items-center justify-center p-4">
      <div className="bg-white rounded-lg max-w-4xl w-full max-h-[90vh] overflow-y-auto">
        <div className="p-6">
          <div className="flex justify-between items-start mb-4">
            <h2 className="text-2xl font-semibold">{product.name}</h2>
            <button
              onClick={onClose}
              className="text-gray-500 hover:text-gray-700"
            >
              <FaTimes size={24} />
            </button>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
            {/* Product Image */}
            <div className="relative">
              <img
                src={product.image}
                alt={product.name}
                className="w-full h-96 object-cover rounded-lg"
              />
              <div className="absolute top-4 right-4 bg-pink-500 text-white px-3 py-1 rounded-full">
                ${product.price.toFixed(2)}
              </div>
            </div>

            {/* Product Details */}
            <div>
              <div className="flex items-center mb-4">
                <div className="flex items-center">
                  <FaStar className="text-yellow-400 mr-1" />
                  <span className="font-semibold">{product.rating}</span>
                </div>
                <span className="mx-2">â€¢</span>
                <span className="text-gray-600">{product.reviews} reviews</span>
              </div>

              <p className="text-gray-600 mb-6">{product.description}</p>

              <div className="space-y-4">
                <div>
                  <h3 className="font-semibold mb-2">Occasions</h3>
                  <div className="flex flex-wrap gap-2">
                    {product.occasion.map((occ, index) => (
                      <span
                        key={index}
                        className="bg-pink-100 text-pink-600 px-3 py-1 rounded-full text-sm"
                      >
                        {occ}
                      </span>
                    ))}
                  </div>
                </div>

                <div className="flex gap-4">
                  <button
                    onClick={handleAddToCart}
                    className="flex-1 bg-pink-500 text-white py-3 rounded-lg hover:bg-pink-600 transition-colors flex items-center justify-center"
                  >
                    <FaShoppingCart className="mr-2" />
                    Add to Cart
                  </button>
                  <button
                    onClick={handleToggleWishlist}
                    className={`p-3 rounded-lg border ${
                      isInWishlist(product.id)
                        ? 'bg-pink-100 text-pink-500 border-pink-500'
                        : 'border-gray-300 text-gray-600 hover:border-pink-500 hover:text-pink-500'
                    }`}
                  >
                    <FaHeart />
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default QuickViewModal;
