import React, { useState } from 'react';

const productsData = [
  { id: 1, name: 'Rose Delight', price: 20, occasion: 'Birthday' },
  { id: 2, name: 'Tulip Joy', price: 25, occasion: 'Anniversary' },
  { id: 3, name: 'Lily Love', price: 30, occasion: 'Wedding' },
  { id: 4, name: 'Sunflower Shine', price: 15, occasion: 'Get Well Soon' },
  { id: 5, name: 'Orchid Grace', price: 35, occasion: 'Birthday' },
];

const ProductPage = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [occasionFilter, setOccasionFilter] = useState('All');
  const [priceFilter, setPriceFilter] = useState('');

  const filteredProducts = productsData.filter(product => {
    const matchesSearch = product.name.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesOccasion = occasionFilter === 'All' || product.occasion === occasionFilter;
    const matchesPrice = priceFilter === '' || product.price <= parseFloat(priceFilter);
    return matchesSearch && matchesOccasion && matchesPrice;
  });

  return (
    <div className="container mt-5">
      <h2 className="mb-4">Our Products</h2>

      <div className="row mb-4">
        <div className="col-md-4 mb-2">
          <input
            type="text"
            placeholder="Search flowers..."
            className="form-control"
            value={searchTerm}
            onChange={e => setSearchTerm(e.target.value)}
          />
        </div>
        <div className="col-md-4 mb-2">
          <select
            className="form-control"
            value={occasionFilter}
            onChange={e => setOccasionFilter(e.target.value)}
          >
            <option value="All">All Occasions</option>
            <option value="Birthday">Birthday</option>
            <option value="Anniversary">Anniversary</option>
            <option value="Wedding">Wedding</option>
            <option value="Get Well Soon">Get Well Soon</option>
          </select>
        </div>
        <div className="col-md-4 mb-2">
          <input
            type="number"
            placeholder="Max Price"
            className="form-control"
            value={priceFilter}
            onChange={e => setPriceFilter(e.target.value)}
          />
        </div>
      </div>

      <div className="row">
        {filteredProducts.length > 0 ? (
          filteredProducts.map(product => (
            <div className="col-md-4 mb-4" key={product.id}>
              <div className="card h-100">
                <div className="card-body text-center">
                  <h5 className="card-title">{product.name}</h5>
                  <p className="card-text">${product.price}</p>
                  <p className="card-text"><strong>Occasion:</strong> {product.occasion}</p>
                  <button className="btn btn-outline-primary">Add to Cart</button>
                </div>
              </div>
            </div>
          ))
        ) : (
          <p>No matching products found.</p>
        )}
      </div>
    </div>
  );
};

export default ProductPage;
