import React from 'react';

const PageTransition = ({ children }) => {
  return (
    <div className="page-transition">
      {children}
      <style jsx>{`
        .page-transition {
          animation: pageEnter 0.8s ease-out;
        }
        
        @keyframes pageEnter {
          0% {
            opacity: 0;
            transform: translateY(20px);
          }
          100% {
            opacity: 1;
            transform: translateY(0);
          }
        }
      `}</style>
    </div>
  );
};

export default PageTransition;
